import 'dart:convert';
import 'package:flutter/material.dart';

class WhatsAppIcon extends StatelessWidget {
  final double size;
  
  const WhatsAppIcon({Key? key, this.size = 24}) : super(key: key);
  
  // Base64 encoded WhatsApp icon
  static const String _whatsappIconBase64 = '';
  
  @override
  Widget build(BuildContext context) {
    return Image.memory(
      base64Decode(_whatsappIconBase64),
      width: size,
      height: size,
    );
  }
}